import React, { useState } from 'react';

 

const OrderDetails = () => {
  const [formData, setFormData] = useState({
    pizza: 0,
    burgers: 0,
    mojitos: 0,
  });

  const a= localStorage.getItem("customer");

 

  const handleChange = (e) => {
    const { name, value } = e.target;
    // Ensure that the input is a positive number
    setFormData({
      ...formData,
      [name]: value >= 0 ? value : 0,
    });
  };

 

  const handleSubmit = (e) => {
    e.preventDefault();
    // Add form validation logic here
    if (formData.pizza === 0 && formData.burgers === 0 && formData.mojitos === 0) {
      // Display an error message to the user
      alert("Please select at least one item.");
      return;
    }
    // Add further processing here (e.g., sending data to the server)
  };

 

  return (
    <form onSubmit={handleSubmit}><br></br><br></br>
      <fieldset>
        <legend>Order Details</legend>
        <div>
          <label><br></br>
            Pizza:
            <input
              type="number"
              name="pizza"
              value={formData.pizza}
              onChange={handleChange}
            />
          </label><br></br><br></br>
        </div>
        <div>
          <label><br></br>
            Burgers:
            <input
              type="number"
              name="burgers"
              value={formData.burgers}
              onChange={handleChange}
            />
          </label><br></br><br></br>
        </div>
        <div>
          <label><br></br>
            Mojitos:
            <input
              type="number"
              name="mojitos"
              value={formData.mojitos}
              onChange={handleChange}
            />
          </label><br></br><br></br>
        </div><center>
        <button type="submit">Submit Order</button>
        </center>
      </fieldset>
      
    </form>
    
  );
};

 

export default OrderDetails;
