import React from 'react';

const Catalogue = () => {
  // Sample menu data
  const menuItems = [
    { category: 'Starters', name: 'Garlic Bread', price: '$.99' },
    { category: 'Starters', name: 'Mozzarella Sticks', price: '$7.99' },
    { category: 'Main Course', name: 'Spaghetti Carbonara', price: '$12.99' },
    { category: 'Main Course', name: 'Margherita Pizza', price: '$10.99' },
    { category: 'Desserts', name: 'Tiramisu', price: '$6.99' },
    { category: 'Desserts', name: 'Chocolate Cake', price: '$5.99' },
  ];

  // Function to group menu items by category
  const groupByCategory = (items) => {
    return items.reduce((acc, item) => {
      const { category } = item;
      if (!acc[category]) {
        acc[category] = [];
      }
      acc[category].push(item);
      return acc;
    }, {});
  };

  const groupedMenu = groupByCategory(menuItems);

  return (
    <div className="catalogue">
      {Object.keys(groupedMenu).map((category, index) => (
        <div key={index}>
          <h2>{category}</h2>
          <ul>
            {groupedMenu[category].map((menuItem, itemIndex) => (
              <li key={itemIndex}>
                <span className="item-name">{menuItem.name}</span>
                <span className="item-price">{menuItem.price}</span>
              </li>
            ))}
          </ul>
        </div>
      ))}
    </div>
  );
};

export default OrderDetails;
