import React, { useState } from 'react';

function OrderDetails() {
  const initialFormData = {
    name: '',
    phone: '',
    orderDetails: '',
    quantity: '',
  };

  const [formData, setFormData] = useState(initialFormData);
  const [formErrors, setFormErrors] = useState({});

  const validateForm = () => {
    let errors = {};
    let isValid = true;

    if (!formData.name.trim()) {
      errors.name = 'Name is required';
      isValid = false;
    }

    const phonePattern = /^\d{10}$/;
    if (!phonePattern.test(formData.phone)) {
      errors.phone = 'Phone number must be 10 digits';
      isValid = false;
    }

    if (!formData.orderDetails.trim()) {
      errors.orderDetails = 'Order details are required';
      isValid = false;
    }

    const quantityValue = parseInt(formData.quantity);
    if (isNaN(quantityValue) || quantityValue <= 0) {
      errors.quantity = 'Quantity must be a positive number';
      isValid = false;
    }

    setFormErrors(errors);
    return isValid;
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      // Here you can add your form submission logic
      // For example, you can send the formData to a server
      console.log('Form submitted:', formData);
      // Reset the form after submission
      setFormData(initialFormData);
      setFormErrors({});
    }
  };

  const handleReset = () => {
    setFormData(initialFormData);
    setFormErrors({});
  };

  return (
    <div>
      <center>
        <form onSubmit={handleSubmit}>
          Name:
          <input
            type="text"
            placeholder="Enter your name"
            name="name"
            value={formData.name}
            onChange={handleInputChange}
            required
          />
          <span className="error">{formErrors.name}</span>
          <br /><br />

          Phone:
          <input
            type="text"
            placeholder="Enter your phone number"
            name="phone"
            value={formData.phone}
            onChange={handleInputChange}
            required
          />
          <span className="error">{formErrors.phone}</span>
          <br /><br />

          Order Details:
          <textarea
            rows="4"
            cols="30"
            placeholder="Enter your order details"
            name="orderDetails"
            value={formData.orderDetails}
            onChange={handleInputChange}
            required
          ></textarea>
          <span className="error">{formErrors.orderDetails}</span>
          <br /><br />

          Quantity:
          <input
            type="number"
            placeholder="Enter quantity"
            name="quantity"
            value={formData.quantity}
            onChange={handleInputChange}
            required
          />
          <span className="error">{formErrors.quantity}</span>
          <br /><br />

          <input type="submit" value="Submit" />
          <input type="button" value="Reset" onClick={handleReset} />
        </form>
      </center>
    </div>
  );
}

export default OrderDetails;
