import { useState } from "react";

class Customer extends Component{

    
    
    render(){
        return(
            function Customer() {
            const [formData, setFormData] = useState({
              firstName: '',
              lastName: '',
              address: '',
              phoneNumber: '',
              email: '',
            });
          
           
          
            const [errors, setErrors] = useState({});
          
           
          
            const handleChange = (e) => {
              const { id, value } = e.target;
              setFormData((prevData) => ({
                ...prevData,
                [id]: value,
              }));
            };
          
           
          
            const handleSubmit = (e) => {
              e.preventDefault();
              const validationErrors = {};
          
        
              if (!formData.firstName.trim()) {
                validationErrors.firstName = 'First name is required';
              }
          
           
              if (!formData.lastName.trim()) {
                validationErrors.lastName = 'Last name is required';
              }
          
              const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
              if (!formData.email.match(emailPattern)) {
                validationErrors.email = 'Invalid email address';
              }
          
           
              if (!formData.phoneNumber.match(/^\d{10}$/)) {
                validationErrors.phoneNumber = 'Phone number must be 10 digits';
              }
          
              setErrors(validationErrors);
          
           
              if (Object.keys(validationErrors).length === 0) {
                alert('Form submitted successfully');
              }
            };

           
        <div><center>
            First Name: <input type="text" placeholder="Enter your first name" id="firstname" required/><br></br><br></br>
            Last Name: <input type="text" placeholder="Enter your last name" id="lastname" required/><br></br><br></br>
            Address: <br></br><textarea rows="4" cols="30">Enter Address</textarea><br></br>
            Phone No: <input type="text"  maxLength="10" placeholder="Enter your Mobile Number"  id="number" required/><br></br><br></br>
            E-mail: <input type="text" placeholder="Enter your E mail" id="email" required /><br></br><br></br>
            <input type="submit" value="ADD"  onSubmit="return Customer()"/> <input type="submit"  value="RESET"/>
            </center></div>
            }
        
        )
    }
}

export default Customer;
