import React, { useState } from 'react';
import Catalogue from './OrderDetails';

function FoodForm() {
  const [formData, setFormData] = useState({
    category: 'veg',
    vegFood: [],
    nonVegFood: []
  });

  const handleCheckboxChange = (category, value) => {
    setFormData(prevState => ({
      ...prevState,
      [category]: prevState[category].includes(value)
        ? prevState[category].filter(item => item !== value)
        : [...prevState[category], value]
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission here with formData
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <div>
          Food Category:
          <select
            value={formData.category}
            onChange={(e) => setFormData({ ...formData, category: e.target.value })}
          >
            <option value="veg">Veg</option>
            <option value="non-veg">Non - Veg</option>
          </select>
        </div>

        <div>
          Food:
          <ul>
            {formData.category === 'veg' ? (
              ['Paneer butter masala', 'Channa Masala', 'butter naan', 'mushroom rice'].map(food => (
                <li key={food}>
                  <label>
                    <input
                      type="checkbox"
                      checked={formData.vegFood.includes(food)}
                      onChange={() => handleCheckboxChange('vegFood', food)}
                    />
                    {food}
                  </label>
                </li>
              ))
            ) : (
              ['Chicken gravy', 'Briyani', 'prawn'].map(food => (
                <li key={food}>
                  <label>
                    <input
                      type="checkbox"
                      checked={formData.nonVegFood.includes(food)}
                      onChange={() => handleCheckboxChange('nonVegFood', food)}
                    />
                    {food}
                  </label>
                </li>
              ))
            )}
          </ul>
        </div>

        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default Catalogue;
